mapper.select:
SELECT *
  FROM chat_room
 WHERE partnership_member_idx = #{partnershipMemberIdx}
AND delete_date IS NULL

    ORDER BY

        ${order.property} ${order.direction}


LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT *
  FROM chat_room
 WHERE partnership_member_idx = #{partnershipMemberIdx}
   AND idx = #{chatRoomIdx}
   AND delete_date IS NULL

mapper.delete:
UPDATE chat_room
   SET delete_date = NOW()
 WHERE idx = #{idx}
