mapper.select:
SELECT COALESCE(MAX(sort_level), 0) AS max_sort_level
  FROM partnership_position
 WHERE partnership_idx = #{partnershipIdx};

SELECT COUNT(*) > 0
  FROM partnership_member pm
  JOIN member m ON pm.member_idx = m.idx
 WHERE pm.partnership_idx = #{partnershipIdx}
   AND m.email = #{email}

SELECT EXISTS (
    SELECT 1
    FROM partnership_invited_member
    WHERE partnership_idx = #{partnershipIdx}
      AND email = #{email}
)
