mapper.select:
SELECT
    *
FROM project
WHERE idx = #{projectIdx}
AND project_category_idx = #{projectCategoryIdx}

mapper.delete:
DELETE FROM project
WHERE idx = #{projectIdx}
  AND project_category_idx = #{projectCategoryIdx}

mapper.select:
SELECT
    *
FROM project
WHERE project_category_idx = #{projectCategoryIdx}
  AND partnership_idx = #{partnershipIdx}
  AND delete_date is null

    ORDER BY

        ${order.property} ${order.direction}


LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

mapper.update:
UPDATE project
SET project_category_idx = #{projectCategoryIdx}
WHERE idx = #{idx}

UPDATE FROM project
SET delete_date = now()
WHERE idx = #{idx}

mapper.select:
SELECT *
FROM project
WHERE partnership_idx = #{projectId.partnershipIdx}
AND project_category_idx = #{projectId.projectCategoryIdx}

    ORDER BY

        ${order.property} ${order.direction}


LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT COUNT(*)
FROM project
WHERE partnership_idx = #{partnershipIdx}
AND project_category_idx = #{projectCategoryIdx}
