mapper.update:
UPDATE project
    SET delete_date = now()
WHERE idx = #{projectIdx}

mapper.select:
SELECT
    *
FROM project
WHERE project_category_idx = #{projectCategoryIdx}
  AND partnership_idx = #{partnershipIdx}
  AND delete_date is null

    ORDER BY

        ${order.property} ${order.direction}


LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

mapper.update:
UPDATE project
SET project_category_idx = #{projectCategoryIdx}
WHERE idx = #{idx}

mapper.select:
SELECT *
FROM project
WHERE partnership_idx = #{partnershipIdx}
AND project_category_idx = #{projectCategoryIdx}

    ORDER BY

        ${order.property} ${order.direction}


LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

SELECT COUNT(*)
FROM project
WHERE project_category_idx = #{projectCategoryIdx}
AND partnership_idx = #{partnershipIdx}
