mapper.update:
UPDATE project
    SET delete_date = now()
WHERE idx = #{projectIdx}

mapper.select:
SELECT COUNT(*) FROM project a INNER JOIN project_member b ON a.idx = b.project_idx
WHERE a.project_category_idx IS NULL
WHERE a.project_category_idx = #{projectCategoryIdx}
AND b.partnership_member_idx = #{partnershipMemberIdx}

SELECT
    *
FROM project
WHERE project_category_idx = #{projectCategoryIdx}
  AND partnership_idx = #{partnershipIdx}
  AND delete_date is null

    ORDER BY

        ${order.property} ${order.direction}


LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

mapper.update:
UPDATE project
SET project_category_idx = #{projectCategoryIdx}
WHERE idx = #{idx}

mapper.select:
SELECT *
FROM project p
INNER JOIN project_member pm ON pm.project_idx = p.idx
WHERE p.partnership_idx = #{searchProject.partnershipIdx}
AND p.delete_date IS NULL


        AND p.project_category_idx = #{searchProject.projectCategoryIdx}


        AND p.project_category_idx IS NULL


AND pm.partnership_member_idx = #{partnershipMemberIdx}

    ORDER BY

        p.${order.property} ${order.direction}


LIMIT #{pageable.pageSize} OFFSET #{pageable.offset}

