mapper.select:
SELECT
    *
FROM em_saas.project_member
WHERE project_idx = #{projectIdx}
  AND partnership_member_idx = #{partnershipMemberIdx}
    AND delete_date IS NULL

mapper.update:
UPDATE project_member
SET delete_date = NOW(), update_date = NOW()
WHERE project_idx = #{projectIdx}
AND idx IN

    #{id}

AND delete_date IS NULL

mapper.select:
SELECT EXISTS(
    SELECT 1 FROM project_member
    WHERE project_idx = #{projectIdx}
      AND partnership_member_idx = #{partnershipMemberIdx}
      AND delete_date IS NOT NULL
)
